TRUNCATE TABLE POBJECTS
;
INSERT INTO POBJECTS(ID,OBJECTTYPE,CODE,Expiredate,PERSONS_ID_UPD)

SELECT POBJECTs_seq.NEXTVAL,'TABLE', TABLE_NAME,'2099/12/31',1
FROM USER_ALL_TABLES  A
WHERE NOT EXISTS (SELECT 1 FROM POBJECTS B WHERE B.CODE = A.TABLE_NAME
                  AND OBJECTTYPE = 'TABLE' )
;

INSERT INTO POBJECTS(ID,OBJECTTYPE,CODE,Expiredate,PERSONS_ID_UPD)
SELECT POBJECTs_seq.NEXTVAL,'SCREEN_NAME', view_name
,'2099/12/31',1
FROM USER_VIEWS A
WHERE NOT EXISTS (SELECT 1 FROM POBJECTS B WHERE B.CODE = A.view_NAME
                  AND OBJECTTYPE = 'SCREEN_NAME' )
;


