ALTER TABLE Pobjects modify(CharType VARCHAR(10))
;

INSERT INTO POBJECTS(ID,OBJECTTYPE,CODE,PERSONS_ID_UPD,CREATED_AT,EXPIREDATE,UPDATE_AT)
SELECT POBJECTS_SEQ.NEXTVAL,(SELECT * FROM (SELECT 'T',SUBSTR(SCREEN_VIEWNAME,3,20),0,SYSDATE,TO_DATE('2099/12/31','YYYY/MM/DD'),SYSDATE
 FROM R_DETAILFIELDS GROUP BY SCREEN_VIEWNAME)) FROM DUAL
;
INSERT INTO POBJECTS(ID,OBJECTTYPE,CODE,PERSONS_ID_UPD,CREATED_AT,EXPIREDATE,UPDATED_AT)
SELECT Pobjects_seq.NEXTVAL,A.* FROM
(SELECT 'T' A,SUBSTR(SCREEN_VIEWNAME,3,20) B,0 C,SYSDATE,TO_DATE('2099/12/31','YYYY/MM/DD') D,SYSDATE E
 FROM R_DETAILFIELDS GROUP BY SCREEN_VIEWNAME) A
 ;
 
